import Testimonial from "../testimonial.json";

const testItemStyle = {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignContent: "center"
}
const containerStyle = {
    display:"flex",
    flexDirection:"row",
    justifyContent:"space-evenly",
    alignSelf:"center",
    flexWrap:"wrap",
    maxWidth:1000,
    margin: "auto"
}
const testNameStyle = {
    textAlign: "center",
    padding:10,
    fontSize:24,
}
const testStyleStyle = {
    textAlign: "center",
    padding:4,
    fontSize:18,
}
const testTechStyle = {
    textAlign: "center",
    padding:4,
    fontSize:18,
}
const testDimStyle = {
    textAlign: "center",
    padding:4,
    fontSize:18,
}
const testImgStyle = {
    width:465,
}


function testimonialItem(test){
    var name = test.name;
    var style = test.testimonial;
    return(
        <div style = {testItemStyle}>
            
            <div style = {testNameStyle}>{name}</div>
            {imgSrc ? <img style = {testImgStyle} src = {require(""+imgSrc)}></img>:null}
            <a style = {testStyleStyle}>{"Style: " + style}</a>
            <a style = {testTechStyle}>{"Technology: " + tech}</a>
            <a style = {testDimStyle}>{"Dimensions: " + dimension}</a>
            
        </div>
        
    );
}
export default function Testimonials(){
    console.log(tests.toString());
    return(
        <div style = {containerStyle}>
            {tests.map(test=>testimonialItem(test))}
        </div>
    );
}
