import React from 'react';
import splashSrc from './splash.jpg';

class Splash extends React.Component {
  constructor(props){
    super(props);

    this.img = new Image();
    this.state = {
      imgWidth:0,
      imgHeight:0,
      width:0,
      height:0,
      horizontal:0
    }
    this.updateDim = this.updateDim.bind(this);
    this.onImgLoad = this.onImgLoad.bind(this);
  }

  componentDidMount(){
    this.updateDim();
    window.addEventListener('resize', this.updateDim);
  }

  updateDim(){
    this.setState({width:window.innerWidth,height:window.innerHeight})
    if(this.state.width/this.state.height < this.state.imgWidth/this.state.imgHeight){
      this.setState({horizontal:true});
    }else {
      this.setState({horizontal:false});
    }
  }

  async onImgLoad({target:img}) {
    await this.setState({imgHeight: img.offsetHeight, imgWidth: img.offsetWidth});
    this.updateDim();
  }
  render(){
    return (
      <div className = {splash}>
        <div className = {splashImgContainer}>
          <a className = {splashText}>{"Alex Salazar"}</a>
          <img onLoad = {this.onImgLoad} className = {this.state.horizontal?{splashImg1}:{splashImg2}} src = {splashSrc} alt="Logo"></img> 
        </div>
      </div>
  )
}
}
const splash = {
    minHeight: "75vh",
  }
const splashImgContainer =  {
    marginTop:"10vh",
    maxHeight:"20",
    height:"100vh",
    overflow: "hidden",
    position: "relative",
    textAlign: "center",
  }
const splashText ={
    top:"30%",
    width:"100%",
    left:0,
    color: "white",
    position: "absolute",
    //fontFamily:  $fontFamily,
      fontSize: "10vw",
  }
const splashImg1 ={
    height:"100%",
    width:"auto",
  }
const splashImg2= {
    height:"auto",
    width:"100%",
  }
export default Splash;
